import React, {Component} from 'react';
import { Table } from 'semantic-ui-react'
import axios from 'axios'

export default class ViewUser extends Component {

    state = {
      users: []
    }

    componentDidMount(){
      axios.get('http://localhost:8080/Admin/getUsers',{
      })
      .then((response) => {
        if(response.data.msg){
          this.setState({users: response.data.table.rows});
        }
        else{
          console.log("Did");
        }
      });

      console.log(this.state.users);
    }

    render(){
        let users = this.state.users;
        return(
            <div>
            <table class="table">
              <thead class="thead-light">
                <tr>
                  <th scope="col">#</th>
                  <th scope="col">First</th>
                  <th scope="col">Last</th>
                  <th scope="col">Handle</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user =><tr scope="row" key={user.vehiNo}>
                    <td>{user.nameInitials}</td>
                    <td>{user.vehiNo}</td>
                    <td>{user.chassis}</td>
                    <td>{user.capacity}</td>
                    <td>{user.make+' '+user.modal}</td>
                    {/* <td><button onClick={this.update.bind(this, vehicle.customer_id)}>Edit</button>|<button onClick={this.removeCountry.bind(this, vehicle.customer_id)}>Remove</button></td> */}
                </tr>
                )}
                // <tr>
                //   <th scope="row">1</th>
                //   <td>Mark</td>
                //   <td>Otto</td>
                //   <td>@mdo</td>
                // </tr>
              </tbody>
            </table>
            </div>
        )
    }
}
