import React, {Component} from 'react'
import axios from 'axios'
import $ from 'jquery'
import {ToastsContainer, ToastsStore, ToastsContainerPosition} from 'react-toasts';
import 'simplebar'; // or "import SimpleBar from 'simplebar';" if you want to use it manually.
import 'simplebar/dist/simplebar.css';
import { MDBNavLink } from "mdbreact";

import CustomerDetails from './SubComponent/CustomerDetails'
import VehicleDetails from './SubComponent/VehicleDetails'
import Payment from './SubComponent/Payment'

const expArr = [false, false, false, false];


export default class Agreement extends Component{

    constructor(props){
        super(props)
        this.state = {
          values: [],
          isSearch: false,
          logo: false,
          expand: 0,
          show: false,
          count: 0,
          saveIcon: 3
        }
    }

    setAgreement(data){
        this.setState({
          agreementData: data,
          agreeid: null,
          created: null,
          version: null,
        })
    }


    componentDidMount(){
        localStorage.setItem('agreement', null);
    }

    getAgreement(){
        var self = this;
        localStorage.setItem('agreement', null);
        var agreeNo = $('#inputAgrrNo').val().toUpperCase();
        var path = 'http://localhost:8080/Agreement/getData';

        if(agreeNo.length > 9){
            axios.post(path, {
                data: agreeNo
              })
              .then(function (response) {
                if(response.data.msg){
                    if(response.data.table.rowCount > 1){
                        $('.checkButton').css('display','block')
                    }
                    localStorage.setItem('agreement', JSON.stringify(response.data.table.rows[0]));
                    self.setState({values: response.data.table.rows[0], isSearch: true, agreeid: response.data.agreeid, created: response.data.created, version: response.data.version})
                    $('.genarate').css('display','block')
                    if (response.data.isRow) {
                      localStorage.setItem('agreeid', self.state.agreeid);
                      localStorage.setItem('created', self.state.created);
                      localStorage.setItem('version', self.state.version);
                    }
                }else{
                    ToastsStore.error("User Data Is Not Found")
                }
              })
              .catch(function (error) {
                console.log(error);
                ToastsStore.error('Connection Fail')
              });
        }else{
            ToastsStore.warning("Invalid Vehical No or Agreement ID")
        }

    }

    logoShow(value){
        if(this.state.expand == value && this.state.count == 1){
            this.setState({expand: value, show: false, count: 0, saveIcon: false});
            expArr[value-1] = false;
        }else{
            this.setState({expand: value, show: true, count: 1});
            expArr[value-1] = true;
            if(this.state.expand == 3){
                this.setState({saveIcon: true});
            }
        }
        console.log(expArr);
        
    }

    render(){
      var data = this.state.values;
        return(
            <div className="page-content">
                <div className="col-md-12 col-sm-12 col-xs-12 topItem text-dark">
                    <div className="itemTitle" onClick={() => this.logoShow(1)}>
                        <div className="row">
                            <div className="col-md-10 col-sm-10 col-xs-10">
                                {this.state.expand == 1 && this.state.show
                                    ? <i class="fa fa-angle-down arrowicon" style={{fontSize: '20px'}}></i>
                                    : <i class="fa fa-angle-right arrowicon" style={{fontSize: '20px'}}></i>
                                }
                                <h4 className="uploadTitle">Customer Details</h4>
                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">

                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">
                                {this.state.saveIcon && this.state.expand == 1
                                    ? <i className="fa fa-save saveicon" style={{fontSize: '20px'}}></i>
                                    : ""
                                }
                            </div>
                        </div>
                    </div>
                        {this.state.expand == 1 && this.state.show || expArr[0]
                            ? <CustomerDetails/>
                            : ""
                        }
                </div>
                <div className="col-md-12 col-sm-12 col-xs-12 middelItem text-dark">
                    <div className="itemTitle" onClick={() => this.logoShow(2)}>
                        <div className="row">
                            <div className="col-md-10 col-sm-10 col-xs-10">
                                {this.state.expand == 2 && this.state.show
                                    ? <i class="fa fa-angle-down arrowicon" style={{fontSize: '20px'}}></i>
                                    : <i class="fa fa-angle-right arrowicon" style={{fontSize: '20px'}}></i>
                                }
                                <h4 className="uploadTitle">Vehicle Details</h4>
                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">

                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">
                                {this.state.saveIcon && this.state.expand == 2
                                    ? <i className="fa fa-save saveicon" style={{fontSize: '20px'}}></i>
                                    : ""
                                }
                            </div>
                        </div>
                    </div>
                        {this.state.expand == 2 && this.state.show || expArr[1]
                            ? <VehicleDetails/>
                            : ""
                        }
                </div>
                <div className="col-md-12 col-sm-12 col-xs-12 middelItem text-dark">
                    <div className="itemTitle" onClick={() => this.logoShow(3)}>
                        <div className="row">
                            <div className="col-md-10 col-sm-10 col-xs-10">
                                {this.state.expand == 3 && this.state.show
                                    ? <i class="fa fa-angle-down arrowicon" style={{fontSize: '20px'}}></i>
                                    : <i class="fa fa-angle-right arrowicon" style={{fontSize: '20px'}}></i>
                                }
                                <h4 className="uploadTitle">Payment Details</h4>
                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">

                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">
                                {this.state.saveIcon && this.state.expand == 3
                                    ? <i className="fa fa-save saveicon" style={{fontSize: '20px'}}></i>
                                    : ""
                                }
                            </div>
                        </div>
                    </div>
                        {this.state.expand == 3 && this.state.show || expArr[2]
                            ? <Payment/>
                            : ""
                        }
                </div>
                <div className="col-md-12 col-sm-12 col-xs-12 bottemItem text-dark">
                    <div className="itemTitle" onClick={() => this.logoShow(4)}>
                        <div className="row">
                            <div className="col-md-10 col-sm-10 col-xs-10">
                                {this.state.expand == 4 && this.state.show
                                    ? <i class="fa fa-angle-down arrowicon" style={{fontSize: '20px'}}></i>
                                    : <i class="fa fa-angle-right arrowicon" style={{fontSize: '20px'}}></i>
                                }
                                <h4 className="uploadTitle">Other Details</h4>
                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">

                            </div>
                            <div className="col-md-1 col-sm-1 col-xs-1">
                                {this.state.saveIcon && this.state.expand == 4
                                    ? <i className="fa fa-save saveicon" style={{fontSize: '20px'}}></i>
                                    : ""
                                }
                            </div>
                        </div>
                    </div>
                        {this.state.expand == 4 && this.state.show || expArr[0]
                            ? <CustomerDetails/>
                            : ""
                        }
                </div>
            </div>
        )
    }
}
